	class Solution {
		public boolean wordBreak(String s, List<String> wordDict) {
	    		if(s.isEmpty() || wordDict.isEmpty()) {
	    			return false;
	    		}
	    		
	    		TreeMap<Integer, List<Integer>> map = new TreeMap<>();
	    		
	    		for(String word : wordDict) {
	    			List<Integer> startIndices = indexOf(word, s);
	    			if(!startIndices.isEmpty()) {
	    				for(Integer index : startIndices) {
	    					List<Integer> list = map.getOrDefault(index, new ArrayList<>());
	                    list.add(index+word.length());
	                    Collections.sort(list);
						map.put(index, list);
	    				}
	    			}
	    		}
	    		
	    		for(Map.Entry<Integer, List<Integer>> entry : map.entrySet()) {
	    			System.out.println(">> key: " + entry.getKey() + ", value: " + entry.getValue().toString());
	    		}
	    		
	        return checkContinuity(map, s.length());
	    }
    
		public boolean checkContinuity(TreeMap<Integer, List<Integer>> map, int last) {
			
			if(map.isEmpty())
				return false;
			
			int first = map.firstKey();
			Integer key = null;
			
			for(Integer num : map.get(first)) {
				key = num;
				while(key != null && map.containsKey(key)) {
					List<Integer> values = map.get(key);
					key = values.get(0);
				}
				
				if(key == last)
					return true;
			}
			
	    		return false;
	    }
	    
	    public List<Integer> indexOf(String word, String given) {
	    		List<Integer> indexList = new ArrayList<>();
	    		int index = given.indexOf(word);
	    		
	    		if(index == -1)
	    			return indexList;
	    		
	    		indexList.add(index);
	    		
	    		while(index != -1) {
	    			index = given.indexOf(word, index+1);
	    			if(index != -1)
	    				indexList.add(index);
	    		}
	    		
	    		return indexList;
	    }
	}
